import React, { useState } from 'react';

export default function EmployeeForm() {
    const [employeeName, setEmployeeName] = useState('');
    const [employeeID, setEmployeeID] = useState('');
    const [employeeEmail, setEmployeeEmail] = useState('');
    const [employeePH, setEmployeePH] = useState('');
    const [department, setDepartment] = useState('');
    const [dateOfJoining, setDateOfJoining] = useState('');
    const [role, setRole] = useState('');
    const [error, setError] = useState('');

    const handleSubmit = (event) => {
        event.preventDefault();
        if (
            !employeeName ||
            !employeeID ||
            !employeeEmail ||
            !employeePH ||
            !department ||
            !dateOfJoining ||
            !role
        ) {
            setError('All fields are required. Please fill them in.');
            return;
        }
        setError('');
        console.log(`Employee Name: ${employeeName}`);
        console.log(`Employee ID: ${employeeID}`);
        console.log(`Employee Email: ${employeeEmail}`);
        console.log(`Phone Number: ${employeePH}`);
        console.log(`Department: ${department}`);
        console.log(`Date of Joining: ${dateOfJoining}`);
        console.log(`Role: ${role}`);
    };

    const handleReset = () => {
        setEmployeeName('');
        setEmployeeID('');
        setEmployeeEmail('');
        setEmployeePH('');
        setDepartment('');
        setDateOfJoining('');
        setRole('');
        setError('');
    };

    return (
        <form onSubmit={handleSubmit}>
            {error && <p style={{ color: 'red' }}>{error}</p>}

            <label>
                Employee Name: <span style={{ color: 'red' }}>*</span>
                <input
                    type="text"
                    value={employeeName}
                    onChange={(e) => setEmployeeName(e.target.value)}
                    placeholder="Enter employee name"
                    required
                />
            </label>
            <br />
            <br />

            <label>
                Employee ID: <span style={{ color: 'red' }}>*</span>
                <input
                    type="text"
                    value={employeeID}
                    onChange={(e) => setEmployeeID(e.target.value)}
                    placeholder="Enter employee ID"
                    required
                />
            </label>
            <br />
            <br />

            <label>
                Email: <span style={{ color: 'red' }}>*</span>
                <input
                    type="email"
                    value={employeeEmail}
                    onChange={(e) => setEmployeeEmail(e.target.value)}
                    placeholder="abc@gmail.com"
                    required
                />
            </label>
            <br />
            <br />

            <label>
                Phone Number: <span style={{ color: 'red' }}>*</span>
                <input
                    type="number"
                    value={employeePH}
                    onChange={(e) => setEmployeePH(e.target.value)}
                    placeholder="8072802174"
                    required
                />
            </label>
            <br />
            <br />

            <label>
                Department: <span style={{ color: 'red' }}>*</span>
                <select
                    value={department}
                    onChange={(e) => setDepartment(e.target.value)}
                    required
                >
                    <option value="">Select Department</option>
                    <option value="HR">HR</option>
                    <option value="Engineering">Engineering</option>
                    <option value="Marketing">Marketing</option>
                </select>
            </label>
            <br />
            <br />

            <label>
                Date of Joining: <span style={{ color: 'red' }}>*</span>
                <input
                    type="date"
                    value={dateOfJoining}
                    onChange={(e) => setDateOfJoining(e.target.value)}
                    max={new Date().toISOString().split('T')[0]}
                    required
                />
            </label>
            <br />
            <br />

            <label>
                Role: <span style={{ color: 'red' }}>*</span>
                <input
                    type="text"
                    value={role}
                    onChange={(e) => setRole(e.target.value)}
                    placeholder="Enter role"
                    required
                />
            </label>
            <br />
            <br />

            <button type="submit">Submit</button>
            <button type="button" onClick={handleReset} style={{ marginLeft: '10px' }}>
                Reset
            </button>
        </form>
    );
}
